import React, { useEffect, useState } from "react";

const Home = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const sendMessage = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://192.168.0.104:8000/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ question: inputText }),
      });
      const data = await response.json(); // Parse response JSON
      const newMessages = [
        ...messages,
        { text: inputText, type: "user" },
        { text: data.response, type: "bot" },
      ];
      setMessages(newMessages);
    } catch (error) {
      console.error("Error:", error);
      setMessages([
        ...messages,
        { text: "Error occurred. Please try again later.", type: "bot" },
      ]);
    } finally {
      setLoading(false);
    }
    setInputText(""); // Clear input after sending message
  };
  useEffect(() => {
    // Replace 'your-endpoint' with the actual endpoint path if needed
    fetch("http://101.50.109.67:4000/hello")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setData(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="w-full bg-white">
      <div className="bg-black px-4 py-8">
        <p className="text-[#a19f9f] text-[14px] text-center ">
          Â© 2022-22 REBHUB. All Rights Reserved.
        </p>
      </div>
      <div className="p-4">
        {loading && <p>Loading...</p>}
        {error && <p>Error: {error.message}</p>}
        {data && <pre>{JSON.stringify(data, null, 2)}</pre>}
      </div>
    </div>
  );
};

export default Home;
